import './App.css';
import Button from './components/Button';
import { useState } from 'react';

function App() {

  const [{input,setInput},{previousNumber,setPreviousNumber},{currentNumber,setCurrentNumber},{operator,setOperator}] = useState("","","","");

  addToInput = val => { setInput(input + val); };

  addDecimal = val => {
    if (input.indexOf(".") === -1) {  setInput(input + val); }
  };



  return (
    <div className="App">
      <div className="wrapper">
        <div className="calc-row">
          <Button>7</Button>
          <Button>8</Button>
          <Button>9</Button>
          <Button>/</Button>
        </div>
        <div className="calc-row">
          <Button>4</Button>
          <Button>5</Button>
          <Button>6</Button>
          <Button>*</Button>
        </div>
        <div className="calc-row">
          <Button>1</Button>
          <Button>2</Button>
          <Button>3</Button>
          <Button>+</Button>
        </div>
        <div className="calc-row">
          <Button>.</Button>
          <Button>0</Button>
          <Button>=</Button>
          <Button>-</Button>
        </div>
      </div>
    </div>
  );
}

export default App;
